import React, {ChangeEvent, useState} from 'react';
import './App.css';
import Button from "./Components/Button";
import {Input} from "./Components/Input";

function App() {

    const [startValue, setStartValue] = useState<number>(0)

    const onChangeMinInputHandler = (event: ChangeEvent<HTMLInputElement>) => {
        setStartValue(event.currentTarget.valueAsNumber)
    }

    const [maxValue, setMaxiValue] = useState<number>(startValue)

    const onChangeMaxInputHandler = (event: ChangeEvent<HTMLInputElement>) => {
        setMaxiValue(event.currentTarget.valueAsNumber)
    }

    const addValueButton = () => {
        setStartValue(startValue)
        setMaxiValue(maxValue)
    }

  const [count, setCount] = useState<number>(startValue)

  const onClickAddHandler = () => {
      if (count < maxValue) {
      setCount(count + 1)
      }
  }

  const onClickResetHandler = () => {
    setCount(startValue)
  }

  return (
    <div className="App">
      <div>{count}</div>
        <div>
            <Button onClick={onClickAddHandler} title={'add'}/>
            <Button onClick={onClickResetHandler} title={'reset'}/>
        </div>
            <div>
                <span>start:</span>
                <Input onChange={onChangeMinInputHandler}/>
                <span>max:</span>
                <Input onChange={onChangeMaxInputHandler}/>
            </div>
        <div>
            <Button onClick={addValueButton} title={'set'}/>
        </div>
    </div>
  );
}

export default App;
