import React, {ChangeEvent, useState} from 'react';
import './App.css';
import Button from "./Components/Button";
import {Input} from "./Components/Input";

type disabledCheckProps = 'add' | 'reset'

function App() {

    const zero = 0

    const [startValue, setStartValue] = useState<number>(zero)

    const onChangeStartInputHandler = (e: ChangeEvent<HTMLInputElement>) => {
        /*const startEventValue = e.currentTarget.valueAsNumber
        if(startEventValue >= zero) setStartValue(startEventValue)*/
        setStartValue(e.currentTarget.valueAsNumber)


    }

    const [maxValue, setMaxiValue] = useState<number>(startValue)

    const onChangeMaxInputHandler = (e: ChangeEvent<HTMLInputElement>) => {
        setMaxiValue(e.currentTarget.valueAsNumber)
    }

    const addValueButton = () => {
        setStartValue(startValue)
        setMaxiValue(maxValue)
        setCount(startValue)

    }

  const [count, setCount] = useState<number>(startValue)

  const onClickAddHandler = () => {
      if (count < maxValue) setCount(count + 1)
  }

  const onClickResetHandler = () => {
    setCount(startValue)
  }

  const disabledCheck = (name: disabledCheckProps) => name === 'add' && count !== maxValue  ||  name === 'reset' && count === startValue

  return (
    <div className="App">
      <div>{count}</div>
        <div>
            <Button onClick={onClickAddHandler} color={'blue'} children={'add'} disabled={disabledCheck('add')}/>
            <Button onClick={onClickResetHandler} color={'blue'} children={'reset'} disabled={disabledCheck('reset')}/>
            start: <Input onChange={onChangeStartInputHandler} value={startValue}/>
            max:<Input onChange={onChangeMaxInputHandler} value={maxValue}/>
            <Button onClick={addValueButton} color={'secondary'} children={'set'}/>
        </div>
    </div>
  );
}

export default App;
