import React, {ChangeEvent, useState} from 'react';
import './App.css';
import Button from "./Components/Button";
import {Input} from "./Components/Input";

function App() {

    const [value, setValue] = useState<number>(0)

    const onChangeMinInputHandler = (event: ChangeEvent<HTMLInputElement>) => {
        setValue(event.currentTarget.valueAsNumber)
    }

    const [maxiValue, setMaxiValue] = useState<number>(0)

    const onChangeMaxInputHandler = (event: ChangeEvent<HTMLInputElement>) => {
        setMaxiValue(event.currentTarget.valueAsNumber)
    }

    const addValueButton = () => {
        setValue(value)
        setMaxiValue(maxiValue)
    }

  const minValue = value
  const maxValue = maxiValue

  const [count, setCount] = useState<number>(minValue)

  const onClickAddHandler = () => {
      if (count < maxValue) {
      setCount(count + 1)
      }
  }

  const onClickResetHandler = () => {
    setCount(minValue)
  }

  return (
    <div className="App">
      <div>{count}</div>
        <div>
            <Button onClick={onClickAddHandler} title={'add'}/>
            <Button onClick={onClickResetHandler} title={'reset'}/>
        </div>
            <span>
                <h4>min:</h4>
                <Input onChange={onChangeMinInputHandler}/>
                <h4>max:</h4>
                <Input onChange={onChangeMaxInputHandler}/>
            </span>
        <div>
            <Button onClick={addValueButton} title={'set'}/>
        </div>
    </div>
  );
}

export default App;
