import React, {ChangeEvent, useState} from 'react';
import './App.css';
import Button from "./Components/Button";
import {Input} from "./Components/Input";

function App() {

    const [value, setValue] = useState<number>(0)
    const [maxiValue, setMaxiValue] = useState<number>(0)

    const onChangeInputHandler = (event: ChangeEvent<HTMLInputElement>) => {
        setValue(event.currentTarget.valueAsNumber)
        setMaxiValue(event.currentTarget.valueAsNumber)
    }

    const addValue = () => {
        setValue(value)
        console.log(value)
        setMaxiValue(maxiValue)
        console.log(maxiValue)
    }

  const minValue = value
  const maxValue = maxiValue

  const [count, setCount] = useState<number>(minValue)

  const onClickAddHandler = () => {
      if (count < maxValue) {
      setCount(count + 1)
      }
  }

  const onClickResetHandler = () => {
    setCount(minValue)
  }


  return (
    <div className="App">
      <div>{count}</div>
        <div>
            <Button onClick={onClickAddHandler} title={'add'}/>
            <Button onClick={onClickResetHandler} title={'reset'}/>
        </div>
            <div>
                <Input onChange={onChangeInputHandler}/>
                <Button onClick={addValue} title={'set'}/>
            </div>
    </div>
  );
}

export default App;
